'use strict';

const browserSync = require('browser-sync').create();
const runSequence = require('run-sequence');
const watch = require('gulp-watch');


module.exports = () => {
  browserSync.init({
    files: [
      '../dist/**/*.{js,css}',
      '../examples/**/*.html',
    ],
    ghostMode: false,
    open: false,
    server: {
      baseDir: ['../examples/', '../dist/']
    },
  });

  watch('../src/scss/**/*.scss', () => { runSequence('scss'); });
};
